function Task({task, onRemove}) {
    const handleRemove = () => {
        onRemove()
    }

    return (

        <div >
            <p>{task.title}</p>
            <p>#{task.id} created on {task.date.toLocaleString()}</p>
            <button onClick={handleRemove}>X</button>
        </div>

    )
}

export default Task;
