import { useState } from 'react';
import Form from '../shared-components/form';
import './style.css';

function Todo({ title, numberTasks, clear, tasks, onCreateTask }) {

    const [showForm, setShowForm] = useState(false)


    const handleCreateTask = () => {
        setShowForm(true)
    } 

    const handleAddTask = (e) => {
        onCreateTask(e)
        setShowForm(false)
    }

    const handleCancelTask = () => {
        setShowForm(false)
    }


    return (

        <>
            <div className="container-todo">
                <div className="header">
                    <div className='circle-todo'>
                        <div className="circle">{numberTasks}</div>
                        <p className="todo">{title}</p>
                    </div>
                    <button onClick={handleCreateTask}  className="plus">+</button>
                    {clear && <button>Clear all</button>}
                </div>

                <div className='container-tasks'>
                    {tasks && tasks.map((task, index) => (
                    <div key={index}>
                       <p>{task.title}</p>
                       <p>#{task.id} created on {task.date.toLocaleString()}</p>
                    </div>
                    ))}
                </div>

                {showForm && <Form onAdd={handleAddTask} onCancel={handleCancelTask}></Form>}
            </div>
        </>
    )
}

export default Todo;
