import { useState } from 'react';
import Form from '../shared-components/form';
import Todo from '../todo';
import './style.css'

function Board() {
    const [tasks, setTasks] = useState([])

    const handleCreateTask = (e, status) => {
        const task = {
            id: tasks.length + 1,
            title: e,
            status: status,
            date: new Date()
        }
        setTasks((tasks) => [...tasks, task])
    }

    return (
        <div className='board-wrapper'>
            <Todo title="TODO" numberTasks={1} tasks={tasks.filter(task => task.status === '')} onCreateTask={e => handleCreateTask(e, '')}></Todo>
            <Todo title="IN PROGRESS" numberTasks={1} tasks={tasks.filter(task => task.status === 'pending')} onCreateTask={e => handleCreateTask(e, 'pending')}>  </Todo>
            <Todo title="DONE" numberTasks={1} clear tasks={tasks.filter(task => task.status === 'done')} onCreateTask={e => handleCreateTask(e, 'done')}> </Todo>
        </div>
    )
}

export default Board;
