import './style.css'

function Form ({onCancel, onAdd}){

    const handleCancel = e => {
        e.preventDefault()
        onCancel()
    }

    const handleAdd = e => {
        e.preventDefault()
        onAdd()
    }

    return (

            <form className='wrap-form'>
                <textarea className='text-area' name="" id="" cols="25" rows="3"></textarea>
                <div className='buttons'>
                    <button className='buttons-color button-add' disabled onClick={handleAdd}>Add</button>
                    <button className='buttons-color button-cancel' onClick={handleCancel}>Cancel</button>
                </div>

            </form>

    )
}

export default Form;
